import React, { useState, useEffect } from "react";

import SelectPokemon from "./components/SelectPokemon";

import alakazam from "./assets/images/alakazam.png";
import blastoise from "./assets/images/blastoise.png";
import bulbasaur from "./assets/images/bulbasaur.png";
import charizard from "./assets/images/charizard.png";
import flareon from "./assets/images/flareon.png";
import gengar from "./assets/images/gengar.png";
import haunter from "./assets/images/haunter.png";
import pikachu from "./assets/images/pikachu.png";
import vileplume from "./assets/images/vileplume.png";

const pokemons = [
  "alakazam",
  "blastoise",
  "bulbasaur",
  "charizard",
  "flareon",
  "gengar",
  "haunter",
  "pikachu",
  "vileplume"
];

function App() {
  const [chosen, setChosen] = useState("alakazam");
  const [displayImage, setDisplayImage] = useState(alakazam);
  const [informationPokemon, setInformationPokemon] = useState({});
  const [typePokemon, setTypePokemon] = useState("psyic")

  useEffect(() => {
    async function fetchPokemon() {
      await fetch(`https://pokeapi.co/api/v2/pokemon/${chosen}/`)
        .then(response => {
          return response.json();
        })
        .then(data => {
          setInformationPokemon({ ...informationPokemon, [chosen]: data });
        });
    }
    setTypePokemon(informationPokemon[chosen]?.types[0]?.type?.name);
    fetchPokemon();
  }, [chosen]);

  // useEffect(() => {
  //   switch(informationPokemon[chosen]?.types[0]?.type?.name) {
  //     case "psychic": 
  //     break;
  //     default:
  //       alert('error'); 
  //   }
  // })
  // psychic  water  poison  flying  fire  electric

  console.log(informationPokemon[chosen]?.types[0]?.type?.name);

  function getType(feature) {
    switch (feature) {
      case "speed":
        return informationPokemon[chosen]?.stats[0]?.base_stat;
      case "specialDefense":
        return informationPokemon[chosen]?.stats[1]?.base_stat;
      case "specialAttack":
        return informationPokemon[chosen]?.stats[2]?.base_stat;
      case "defense":
        return informationPokemon[chosen]?.stats[3]?.base_stat;
      case "attack":
        return informationPokemon[chosen]?.stats[4]?.base_stat;
      case "hp":
        return informationPokemon[chosen]?.stats[5]?.base_stat;
      case "weight":
        return informationPokemon[chosen]?.weight;
      case "height":
        return informationPokemon[chosen]?.height;
      case "type":
        return informationPokemon[chosen]?.types[
          informationPokemon[chosen]?.types.length - 1
        ]?.type?.name;
      default:
        return;
    }
  }

  useEffect(() => {
    switch (chosen) {
      case "alakazam":
        setDisplayImage(alakazam);
        break;
      case "blastoise":
        setDisplayImage(blastoise);
        break;
      case "bulbasaur":
        setDisplayImage(bulbasaur);
        break;
      case "charizard":
        setDisplayImage(charizard);
        break;
      case "flareon":
        setDisplayImage(flareon);
        break;
      case "gengar":
        setDisplayImage(gengar);
        break;
      case "haunter":
        setDisplayImage(haunter);
        break;
      case "pikachu":
        setDisplayImage(pikachu);
        break;
      case "vileplume":
        setDisplayImage(vileplume);
        break;
      default:
        alert("error");
    }
  }, [chosen]);

  let habilityHiddenPokemon = informationPokemon[chosen]
  ?.abilities.find((element) => element.is_hidden)
  ?.ability
  ?.name
  ;

 let hability = informationPokemon[chosen]?.abilities?.find((element) => !element.is_hidden)?.ability?.name;

  return (
    <main>
      <div className="container-select">
        <h3>Choose one pokemon</h3>

        <SelectPokemon
          items={pokemons}
          onChange={event => setChosen(event.target.value)}
        />
      </div>

      <div className="display-information">
        <img src={displayImage} alt="pokemon" />

        <section>
          <h3>{chosen}</h3>

          <div className="type-pokemon">
          {informationPokemon[chosen]?.types[0]?.type?.name}
          </div>

          <article className="info-container">
            <ul>
              <li><span>HP:</span> <span>{getType("hp")}</span></li>
              <li><span>ATTACK:</span> <span>{getType("attack")}</span></li>
              <li><span>DEFENSE:</span> <span>{getType("defense")}</span></li>
              <li><span>SPECIAL ATTACK:</span> <span>{getType("specialAttack")}</span></li>
              <li><span>SPECIAL DEFENSE:</span> <span>{getType("specialDefense")}</span></li>
              <li><span>SPEED:</span> <span>{getType("speed")} m/s</span></li>
              <li><span>HEIGHT:</span> <span>{getType("height")} feets</span></li>
              <li><span>WEIGHT:</span> <span>{getType("weight")} pounds</span></li>
            </ul>
          </article>

          <div className="hability-information">
          <article>
            <h4>HABILITY</h4>
            <p>{hability ? hability : "none"}</p>
          </article>

          <article>
            <h4>HIDDEN HABILITY</h4>
            <p>{habilityHiddenPokemon ? habilityHiddenPokemon : "none"}</p>
          </article>
          </div>
        </section>
      </div>
    </main>
  );
}

export default App;
