  }


  return (
    <main>
      <div className="container-select">
      <h3>Choose one pokemon</h3>
      <SelectPokemon items={pokemons} onChange={event => setChosen(event.target.value)} />
      </div>
      <div className="display-information">
      <img src={alakazam} />
      <section>
        <h3>Title</h3>
        <article>
        <p>HP : 84</p>
        <p>ATTACK : 84</p>
        <p>DEFENSE : 78</p>
        <p>SPECIAL ATTACK : 109</p>
        <p>SPECIAL DEFENSE : 85</p>
        <p>SPEED : 100 m/s</p>
        <p>HEIGHT : 17 feets</p>
        <p>WEIGHT : 905 pounds</p>
        </article>


        <span>
        <article>
        <h4>HABILITY</h4>
        <p>Blaze</p>
        </article>

        <article>
        <h4>HIDDEN HABILITY</h4>
        <p>Solar-Power</p>
        </article>
        </span>

      </section>
      </div>
    </main>
  );
}

export default App;
