import React, { useState, useEffect } from "react";

import SelectPokemon from "./components/SelectPokemon";

import alakazam from "./assets/images/alakazam.png";
import blastoise from "./assets/images/blastoise.png";
import bulbasaur from "./assets/images/bulbasaur.png";
import charizard from "./assets/images/charizard.png";
import flareon from "./assets/images/flareon.png";
import gengar from "./assets/images/gengar.png";
import haunter from "./assets/images/haunter.png";
import pikachu from "./assets/images/pikachu.png";
import vileplume from "./assets/images/vileplume.png";

function App() {
  const [chosen, setChosen] = useState("alakazam");
  const [displayImage, setDisplayImage] = useState(alakazam);
  const [informationPokemon, setInformationPokemon] = useState({});
  console.log("informationPokemon", informationPokemon)

  const pokemons = [
    "alakazam",
    "blastoise",
    "bulbasaur",
    "charizard",
    "flareon",
    "gengar",
    "haunter",
    "pikachu",
    "vileplume"
  ];

  useEffect(() => {
  function fetchPokemon() {
    fetch(`https://pokeapi.co/api/v2/pokemon/${chosen}/`)
    .then((response) => {
      return response.json();
    })
    .then((data) => {
      console.log(data);
      setInformationPokemon(data)
    })
  };

  fetchPokemon();
  },[chosen]);

  useEffect(() => {
    switch (chosen) {
      case "alakazam":
        setDisplayImage(alakazam);
        break;
      case "blastoise":
        setDisplayImage(blastoise);
        break;
      case "bulbasaur":
        setDisplayImage(bulbasaur);
        break;
      case "charizard":
        setDisplayImage(charizard);
        break;
      case "flareon":
        setDisplayImage(flareon);
        break;
      case "gengar":
        setDisplayImage(gengar);
        break;
      case "haunter":
        setDisplayImage(haunter);
        break;
      case "pikachu":
        setDisplayImage(pikachu);
        break;
      case "vileplume":
        setDisplayImage(vileplume);
        break;
      default:
        console.log("error");
    }
  }, [chosen]);

  return (
    <main>
      <div className="container-select">

        <h3>Choose one pokemon</h3>

        <SelectPokemon
          items={pokemons}
          onChange={event => setChosen(event.target.value)}
        />
      </div>

      <div className="display-information">

        <img src={displayImage} alt="pokemon"/>

        <section>
          <h3>{chosen}</h3>
          <div className="type-pokemon">Category</div>

          <article className="info-container">
            <ul>
            <li>HP: 84</li>
            <li>ATTACK: 84</li>
            <li>DEFENSE: 78</li>
            <li>SPECIAL ATTACK: 109</li>
            <li>SPECIAL DEFENSE: 85</li>
            <li>SPEED: 100 m/s</li>
            <li>HEIGHT: 17 feets</li>
            <li>WEIGHT: 905 pounds</li>
            </ul>
          </article>

            <article>
              <h4>HABILITY</h4>
              <p>Blaze</p>
            </article>

            <article>
              <h4>HIDDEN HABILITY</h4>
              <p>Solar-Power</p>
            </article>

        </section>
      </div>
    </main>
  );
}

export default App;
