import React, {useState} from "react";

import SelectPokemon from "./components/SelectPokemon";

function App() {
  const [chosen, setChosen] = useState("alakazam");

  const pokemons = [
    "alakazam",
    "blastoise",
    "bulbasaur",
    "charizar",
    "flareon",
    "gengar",
    "haunter",
    "pikachu",
    "vileplume"
  ];

  return (
    <main>
      <div className="container-select">
      <h2></h2>
      <SelectPokemon items={pokemons} onChange={event => setChosen(event.target.value)} />
      </div>
    </main>
  );
}

export default App;
